var hash=location.hash.replace("#","");window.location.hash="";$(function(){$(window).load(function(){if(hash!=""){$("html, body").animate({scrollTop:$("#"+hash).offset().top},"slow",function(){window.location.hash=hash})}});$("a.scroll-to").each(function(){var targetId=$(this).attr("href");if(targetId.indexOf("/")===0)return;var target=$(targetId);$(this).click(function(e){e.preventDefault();$("html,body").animate({scrollTop:target.offset().top},"slow",function(){window.location.hash=targetId})})});$(window).bind("hashchange",function(e){e.preventDefault()})});